import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState('');
  const [clothingSuggestion, setClothingSuggestion] = useState('');

  const API_KEY = 'fd953eddf6f83f966d9240b60bd6fb26';

  const getWeather = async () => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}`
      );

      setWeather(response.data);

      // Your logic for clothing suggestions based on weather goes here
      setClothingSuggestion(getClothingSuggestion(response.data.weather[0].id));
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  const getClothingSuggestion = (weatherId) => {
    // Your logic for clothing suggestions based on weather ID goes here
    // This is just a simple example, you can customize it based on your needs
    if (weatherId >= 200 && weatherId < 600) {
      return 'Rainy day, grab an umbrella and wear a raincoat!';
    } else if (weatherId >= 600 && weatherId < 700) {
      return 'Snowy day, bundle up with warm layers!';
    } else {
      return 'Weather is fine, wear comfortable clothes!';
    }
  };

  return (
    <div>
      <h1>Weather Clothing Suggestion App</h1>
      <input
        type="text"
        placeholder="Enter city name"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <button onClick={getWeather}>Get Weather</button>

      {weather && (
        <div>
          <h2>Weather in {weather.name}</h2>
          <p>Description: {weather.weather[0].description}</p>
          <p>Temperature: {weather.main.temp}Â°C</p>
          <p>{clothingSuggestion}</p>
        </div>
      )}
    </div>
  );
};

export default App;
