const getClothingSuggestions = (temperature, description) => {
    let suggestions = '';

    if (temperature >= 30) {
      suggestions = "It's hot! Wear light and breathable clothes.";
    } else if (temperature >= 20) {
      suggestions = "It's warm. T-shirts and shorts would be comfortable.";
    } else if (temperature >= 10) {
      suggestions = "It's cool. Consider wearing a light jacket or sweater.";
    } else {
      suggestions = "It's cold. Bundle up with a coat and warm layers.";
    }

    // Additional suggestions based on weather description
    if (description.includes('rain')) {
      suggestions += ' Don’t forget an umbrella!';
    } else if (description.includes('snow')) {
      suggestions += ' Wear waterproof boots and warm gloves.';
    }

    return suggestions;
  };

  export default getClothingSuggestions;
