import React, { useEffect, useState } from 'react';
import axios from 'axios';

const WeatherApp = () => {
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    const apiKey = 'fd953eddf6f83f966d9240b60bd6fb26';
    const city = 'Raleigh';

    axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`)
      .then(response => setWeather(response.data))
      .catch(error => console.error(error));
  }, []);

  const getClothingSuggestions = (temperature, weatherCondition) => {
    let suggestions = '';

    if (temperature > 25) {
      suggestions = 'It\'s hot! Wear shorts and a light t-shirt.';
    } else if (temperature > 15) {
      suggestions = 'Mild weather. Consider wearing jeans and a long-sleeve shirt.';
    } else {
      suggestions = 'Cool weather. Grab a jacket or sweater.';
    }

    // Add more specific suggestions based on weather conditions
    switch (weatherCondition) {
      case 'Rain':
        suggestions += ' Don\'t forget your umbrella!';
        break;
      case 'Snow':
        suggestions += ' Bundle up with a coat, gloves, and a hat.';
        break;
      default:
        break;
    }

    return suggestions;
  };

  return (
    <div>
      {weather && (
        <div>
          <h2>{weather.name}</h2>
          <p>Temperature: {weather.main.temp} Â°C</p>
          <p>Weather: {weather.weather[0].description}</p>
          <p>Suggestions: {getClothingSuggestions(weather.main.temp, weather.weather[0].main)}</p>
        </div>
      )}
    </div>
  );
};

export default WeatherApp;
