// src/components/WeatherCard.js
import React from 'react';

const WeatherCard = ({ weatherData }) => {

  const getIconUrl = (iconCode) => `http://openweathermap.org/img/w/${iconCode}.png`;

  const ClothingSuggestions = ({ temperature, description }) => {
    if (temperature >= 30) {
      return <p>It's hot! Wear light and breathable clothes.</p>;
    } else if (temperature >= 20) {
      return <p>It's warm. T-shirts and shorts would be comfortable.</p>;
    } else if (temperature >= 10) {
      return <p>It's cool. Consider wearing a light jacket or sweater.</p>;
    } else {
      return <p>It's cold. Bundle up with a coat and warm layers.</p>;
    }
  };


  return (
    <div>
      <h2>{weatherData.name}</h2>
      <img src={getIconUrl(weatherData.weather[0].icon)} alt="Weather Icon" />
      <p>{weatherData.weather[0].description}</p>
      <p>Temperature: {weatherData.main.temp}°F</p>
      <p>Feels Like: {weatherData.main.feels_like}°F</p>
      <p>Wind Speed: {weatherData.wind.speed} MPH</p>
      <p>Humidity: {weatherData.main.humidity}%</p>
      <p>Cloud Coverage: {weatherData.clouds.all}%</p>
      <p>Atmospheric Pressure: {weatherData.main.pressure} hPa</p>

      <ClothingSuggestions temperature={weatherData.main.temp} description={weather[0].description} />
    </div>

  );
};



export default WeatherCard;
