import React, { useState, useEffect } from 'react';

const getClothingSuggestions = (temperature, description) => {
  // Clothing suggestions logic...
};

const Aside = ({ zipCode }) => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/weather?zipCode=${zipCode}`);
        const data = await response.json();
        setWeatherData(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching weather data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [zipCode]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!weatherData) {
    return <p>Error fetching weather data</p>;
  }

  const { main, weather } = weatherData;

  return (
    <aside>
      <h3>Clothing Suggestions</h3>
      <p>{getClothingSuggestions(main.temp, weather[0].description)}</p>
    </aside>
  );
};

export default Aside;
